import { Link } from "react-router-dom";

const Card = ({ photo, deletePhoto }) => {
  return (
    <div className="card bg-white hover:shadow-2xl rounded-3xl overflow-hidden p-7 pt-0 xl:pb-0 md:pb-0">
      <img className="card-image pb-4 h-48 w-full object-cover" src={photo.imageUrl} alt={photo.id} />
      <div className="card-content text-gray-700 text-base mb-4 pl-14 text-justify">
        <p className="captions " data-testid="photo-caption">{photo.captions}</p>
        <button className="edit-btn bg-blue-500 hover:bg-green-500 text-white font-bold py-2 px-4 rounded-full ">
          <Link className="bg-blue-500" to={`${photo.id}`}>Edit</Link>
        </button>
        <button className="delete-btn bg-red-600 hover:bg-green-500 text-white font-bold py-2 px-4 rounded-full" onClick={() => deletePhoto(photo.id)}>Delete</button>
      </div>
    </div>

  );
};

export default Card;
